import React from 'react';
import {
  ChakraProvider,
  Container,
  theme,
  Flex,
  Heading,
  Spacer,
  Box,
  Icon,
} from '@chakra-ui/react';

import { BsSun } from 'react-icons/bs';

function App() {
  return (
    <ChakraProvider theme={theme}>
      <Container maxW="100%" p="5">
        <Flex spacing="3" padding="6">
          <Heading fontSize="3xl">Sumant Guha</Heading>
          <Spacer />
          <Box fontSize="3xl">
            <Icon color="yellow.500" as={BsSun} />
          </Box>
        </Flex>
      </Container>
    </ChakraProvider>
  );
}

export default App;
