import React from 'react';
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  Text,
  Button,
} from '@chakra-ui/react';

import TA from '../Pages/ta';

const pageHandler = {
  'CSE 14x Head Teaching Assistant': <TA />,
};

const ContentModal = ({
  props,
  onClose,
  isOpen,
  title,
  description,
  start,
  end,
}) => {
  return (
    <Modal
      {...props}
      onClose={onClose}
      size="6xl"
      isOpen={isOpen}
      justifyContent="center"
      alignItems="center"
      motionPreset="slideInBottom"
      scrollBehavior="inside"
    >
      <ModalOverlay />
      <ModalContent>
        <ModalHeader fontSize="5xl" fontWeight={700}>
          {title}
          <Text
            fontSize="md"
            textTransform="uppercase"
            letterSpacing="0.2rem"
            mt={1}
          >
            {description === undefined ? '' : `${description} |`} {start}{' '}
            {end === null ? '' : `- ${end}`}
          </Text>
        </ModalHeader>

        <ModalCloseButton />
        <ModalBody>{pageHandler[title]}</ModalBody>
        <ModalFooter>
          <Button onClick={onClose}>Close</Button>
        </ModalFooter>
      </ModalContent>
    </Modal>
  );
};

export default ContentModal;
