import React, { useRef, useState, useEffect } from 'react';
import {
  ChakraProvider,
  Box,
  IconButton,
  useColorMode,
  useColorModeValue,
  extendTheme,
  Text,
  SlideFade,
  Fade,
} from '@chakra-ui/react';
import { mode } from '@chakra-ui/theme-tools';
import { FaMoon, FaSun } from 'react-icons/fa';

import Projects from './Pages/Projects';
import Experience from './Pages/Experience';
import AboutMe from './Pages/AboutMe';
import Navbar from './Components/Navbar';
import IntroSection from './Pages/IntroSection';
import Footer from './Components/Footer';
import ParticlesBg from 'particles-bg';
import Loader from './Pages/Loader';

import { motion } from 'framer-motion';

import { CgMouse } from 'react-icons/cg';

const theme = extendTheme({
  styles: {
    global: props => ({
      body: {
        bg: mode('#F7FAFC', '#171923')(props),
        color: mode('#171923', '#F7FAFC')(props),
      },
    }),
  },
});

const App = () => {
  const myRef = useRef(null);
  const MotionButton = motion.custom(IconButton);
  const [isHidden, setIsHidden] = useState('visible');
  const [navbar, setNavbar] = useState(false);
  const [bubbles, setBubbles] = useState(false);

  const disableScroll = () => {
    let x = window.scrollX;
    let y = window.scrollY;
    window.onscroll = () => {
      window.scrollTo(x, y);
    };
  };

  const enableScroll = () => {
    window.onscroll = () => {};
  };

  useEffect(() => {
    disableScroll();
    setInterval(() => {
      enableScroll();
      setIsHidden('none');
    }, 2000);
    setInterval(() => {
      setNavbar(true);
    }, 2500);
    setInterval(() => {
      setBubbles(true);
    }, 3000);
  }, []);

  return (
    <ChakraProvider theme={theme}>
      <Fade in={bubbles}>
        {/* <Box
          position="absolute"
          left={0}
          top={0}
          w="100%"
          h="610vh"
          zIndex="-5"
          opacity={0.4}
        >
          <ParticlesBg type="circle" num={7} />
        </Box> */}
      </Fade>
      <Box>
        <Box
          m="0 auto 0 auto"
          display="flex"
          alignItems="center"
          justifyContent="center"
          maxW="100%"
          h="100vh"
          position="relative"
        >
          <Box position="absolute" bottom="2%" mx="auto" textAlign="center">
            <MotionButton
              whileTap={{ scale: 0.9 }}
              whileHover={{ scale: 1.2 }}
              icon={<CgMouse />}
              variant="none"
              fontSize="1.5rem"
              onClick={() =>
                myRef.current.scrollIntoView({ behavior: 'smooth' })
              }
            />
            <Text fontSize="sm" fontWeight={700}>
              Scroll Down
            </Text>
          </Box>

          <Box width="100%">
            <SlideFade offsetY="-1000px" in={bubbles}>
              <IntroSection
                pl={20}
                ml={10}
                pr={20}
                mr={10}
                textAlign="center"
              />
            </SlideFade>
            <Fade in={navbar}>
              <Navbar position="absolute" left="0" top="0" />
            </Fade>
          </Box>
        </Box>
        <AboutMe scrollRef={myRef} />
        <Projects h="auto" pb="20" w="100%" />
        <Experience h="auto" pb="20" w="100%" />
        <Footer position="absolute" left="0" bottom="0" mb={5} />
      </Box>

      <Loader bg="gray.900" display={isHidden} />
    </ChakraProvider>
  );
};

export const ColorModeSwitcher = props => {
  const { toggleColorMode } = useColorMode();
  const text = useColorModeValue('dark', 'light');
  const SwitchIcon = useColorModeValue(FaMoon, FaSun);

  return (
    <IconButton
      size="md"
      fontSize="3xl"
      aria-label={`Switch to ${text} mode`}
      variant="ghost"
      color="current"
      onClick={toggleColorMode}
      icon={<SwitchIcon />}
      {...props}
    />
  );
};

export default App;
