import React from 'react';
import { Box, Text } from '@chakra-ui/react';

const Footer = props => {
  return (
    <Box marginX="auto" w="100%" {...props}>
      <Box display="flex" justifyContent="flex-start">
        <iframe
          src="https://open.spotify.com/follow/1/?uri=spotify:user:31eivmxvoufp4ywtduhq5lvaqbaa&size=detail&theme=dark&show-count=0"
          width="300"
          height="56"
          scrolling="no"
          frameborder="0"
          allowtransparency="true"
        ></iframe>
      </Box>
      <Box textAlign="center" fontSize="xs" pt={2} fontWeight="300">
        <Text>Designed & developed by Sumant Guha</Text>
        <Text>&copy; Crafted with ❤️ in Seattle</Text>
      </Box>
    </Box>
  );
};

export default Footer;
