import React from 'react';
import {
  ChakraProvider,
  Container,
  theme,
  Flex,
  Heading,
  Spacer,
  Box,
  useColorMode,
  Button,
  Link,
  Center,
  extendTheme,
} from '@chakra-ui/react';
import { BiSun } from 'react-icons/bi';
import { ColorModeSwitcher } from './ColorModeSwitcher';

const config = {
  useSystemColorMode: false,
  initialColorMode: 'dark',
};

const customTheme = extendTheme({ config });

function App() {
  const { colorMode, toggleColorMode } = useColorMode();

  return (
    <ChakraProvider theme={customTheme}>
      <Box maxW="100%">
        <Navbar />
      </Box>
    </ChakraProvider>
  );
}

const Navbar = props => {
  return (
    <Flex spacing="3" px="20" py="10" {...props}>
      <Link fontWeight="700" fontSize="3xl">
        Sumant Guha
      </Link>
      <Spacer />
      <Center spacing="3">
        <MenuItem>Home</MenuItem>
        <MenuItem>Contact</MenuItem>
        <ColorModeSwitcher />
      </Center>
    </Flex>
  );
};

const MenuItem = ({ children }) => {
  return (
    <Box display="block" mr={10}>
      <Link textDecoration="None" fontSize="xl" fontWeight="700">
        {children}
      </Link>
    </Box>
  );
};

export default App;
