import React, { useRef, useEffect } from 'react';
import {
  ChakraProvider,
  theme,
  Flex,
  Spacer,
  Box,
  Link,
  Center,
  Text,
  Heading,
  IconButton,
  useColorModeValue,
  Button,
  Image,
  SimpleGrid,
  extendTheme,
} from '@chakra-ui/react';

import me from './images/me.png';
import workingLight from './images/workingLight.svg';
import workingDark from './images/workingDark.svg';
import ta from './images/teaching.svg';
import ey from './images/ml.svg';
import robot from './images/r.svg';
import data from './images/data.svg';
import tt from './images/tt.svg';

import {
  FaFacebookF,
  FaLinkedinIn,
  FaGithub,
  FaMailBulk,
} from 'react-icons/fa';

import { CgMouse } from 'react-icons/cg';

import { ColorModeSwitcher } from './ColorModeSwitcher';

const config = {
  useSystemColorMode: false,
  initialColorMode: 'light',
};

const customTheme = extendTheme({ config });

function App() {
  const myRef = useRef(null);

  return (
    <ChakraProvider theme={customTheme}>
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
        position="relative"
      >
        <IconButton
          position="absolute"
          bottom="2%"
          icon={<CgMouse />}
          variant="ghost"
          fontSize="1.5rem"
          mx="auto"
          textAlign="center"
          onClick={() => myRef.current.scrollIntoView({ behavior: 'smooth' })}
        />
        <Box width="100%">
          <IntroSection paddingX="20" />
          <ImageSection />
          <Navbar position="absolute" left="0" top="0" />
        </Box>
      </Box>

      <AboutMe scrollRef={myRef} />
      <Experience mb={10} />
      <Projects />
      <Footer position="absolute" left="0" bottom="0" />
    </ChakraProvider>
  );
}

const Projects = props => {
  return (
    <Box {...props} h="auto" pb="20" w="100%">
      <Header description="case studies" title="Projects" />
      <Flex
        justifyContent="center"
        alignItems="center"
        columns={{ sm: 1, md: 2 }}
        px="20"
        pt="10"
        maxW="100%"
        mx="auto"
        flexWrap="wrap"
      >
        <Card
          mr={20}
          mb={20}
          name="Code.it: HighSchool Education Platform"
          year="2020"
          image={tt}
          description="Co Founder"
          size="4xl"
        />

        <Card
          mr={20}
          mb={20}
          name="Code.it: HighSchool Education Platform"
          year="2020"
          image={tt}
          size="4xl"
        />
      </Flex>
    </Box>
  );
};

const Experience = props => {
  return (
    <Box {...props} h="auto" pb="20" w="100%">
      <Header description="my work" title="Work Experience" />
      <Flex
        justifyContent="center"
        alignItems="center"
        columns={{ sm: 1, md: 2 }}
        px="20"
        pt="10"
        maxW="100%"
        mx="auto"
        flexWrap="wrap"
      >
        <Card
          mr={20}
          mb={20}
          name="CSE 14x Teaching Assistant"
          year="2020"
          place="University of Washington"
          image={ta}
          size="5xl"
        />
        <Card
          mb={20}
          name="Machine Learning Intern"
          year="2019"
          place="Ernst & Young"
          image={ey}
          size="5xl"
        />

        <Card
          mr={20}
          mb={20}
          name="Embedded Systems Programmer"
          year="2018-2019"
          place="Husky Robotics"
          image={robot}
          size="5xl"
        />
        <Card
          mb={20}
          name="Data Analytics Intern"
          year="2018"
          place="Mu Sigma"
          image={data}
          size="5xl"
        />
      </Flex>
    </Box>
  );
};

const Card = ({ mr, mb, name, year, place, image, size }) => {
  return (
    <Box mr={mr} mb={mb}>
      <Box position="relative" textAlign="left" color="white">
        <Image borderRadius={20} src={image} maxH="90vh" />
        <Box p="7" fontSize="5xl" position="absolute" top={0} left={0}>
          <Heading
            textTransform="uppercase"
            letterSpacing="0.2rem"
            fontSize="xl"
            fontWeight="300"
          >
            {year}
          </Heading>
          <Heading pt="2" fontSize={size}>
            {name}
          </Heading>
          <Heading
            pt={4}
            textTransform="uppercase"
            letterSpacing="0.2rem"
            fontSize="xl"
            fontWeight="300"
          >
            {place}
          </Heading>
        </Box>
      </Box>
    </Box>
  );
};

const AboutMe = ({ props, scrollRef }) => {
  const image = useColorModeValue(workingLight, workingDark);
  return (
    <Box w="100%" ref={scrollRef} {...props}>
      <SimpleGrid h="100vh" pt="20" columns={{ sm: 1, md: 2 }} spacing={10}>
        <Box>
          <Header description="learn more" title="About Me" />
          <Text
            marginLeft="20"
            mt="10"
            maxW="80%"
            fontSize="lg"
            fontWeight={300}
          >
            Hello! I'm Sumant, a 20 year old undergraduate student based out of
            Seattle, Washington. Welcome to my slice of the web 😀 <br /> <br />
            I am a junior studying electrical and computer engineering at the
            University of Washington. Passionate about promoting inclusive
            education and computational literacy, I work as a head teaching
            assistant at the Paul G Allen School of Computer Science and
            Engineering for the introductory computer science series. Teaching
            has been one of the rewarding part of my college experience yet!
            <br /> <br /> I work on a variety of projects across multiple
            platforms such as: Python, React, Java, ReactNative, JavaScript,
            HTML, CSS and C++. Take a look at some of my projects below to see
            my work 💻
          </Text>
        </Box>
        <Center marginRight="20" maxW="80%" maxH="80%">
          <Image src={image} />
        </Center>
      </SimpleGrid>
    </Box>
  );
};

const Navbar = props => {
  const bg = useColorModeValue('blue.600', 'yellow.200');
  return (
    <Flex px="20" py="10" w="100%" {...props}>
      <Box>
        <Link fontWeight="700" fontSize="3xl">
          Sumant Guha
        </Link>
      </Box>
      <Spacer />
      <Center>
        <MenuItem>Home</MenuItem>
        <MenuItem>Contact</MenuItem>
        <Button
          fontSize="xl"
          fontWeight="700"
          mr={10}
          // colorScheme={bg}
          variant="ghost"
          borderWidth="2px"
          borderColor={bg}
          size="md"
        >
          Resume
        </Button>
        <ColorModeSwitcher />
      </Center>
    </Flex>
  );
};

const MenuItem = ({ children }) => {
  return (
    <Box display="block" mr={10}>
      <Link fontSize="xl" fontWeight="700">
        {children}
      </Link>
    </Box>
  );
};

const IntroSection = props => {
  const bg = useColorModeValue('blue', 'yellow');
  const color = useColorModeValue('blue.600', 'yellow.200');
  const highlightColor = useColorModeValue('red.500', 'blue.200');
  return (
    <Box maxW="70%" {...props}>
      <Box mb="4">
        <Heading fontSize="6xl" color={color}>
          Creating and developing
        </Heading>
        <Heading fontSize="6xl" color={highlightColor}>
          <Text display="inline-block">high quality</Text>{' '}
          <Text display="inline-block" textDecoration="none" color={color}>
            content for
          </Text>
        </Heading>
        <Heading fontSize="6xl" color={color}>
          the internet world
        </Heading>
      </Box>
      <Box mt="4">
        <Text
          textTransform="uppercase"
          letterSpacing="0.2rem"
          fontSize="xl"
          fontWeight="200"
        >
          Sumant Guha
        </Text>

        <Box maxW="70%" mt="2" mx="auto">
          <ButtonGroup bg={bg} name={<FaFacebookF />} />
          <ButtonGroup bg={bg} name={<FaLinkedinIn />} />
          <ButtonGroup bg={bg} name={<FaGithub />} />
          <ButtonGroup bg={bg} name={<FaMailBulk />} />
        </Box>
      </Box>
    </Box>
  );
};

const ButtonGroup = ({ props, bg, name }) => {
  return (
    <IconButton
      mt="10"
      size="lg"
      p="5"
      colorScheme={bg}
      fontSize="30px"
      icon={name}
      mr="2rem"
      {...props}
    />
  );
};

const ImageSection = props => {
  return (
    <Box>
      <Image
        position="absolute"
        maxH="80vh"
        maxW="100%"
        right={0}
        bottom={0}
        src={me}
        alt="Sumant Guha"
        objectFit="cover"
        userdrag="none"
        userSelect="none"
      />
    </Box>
  );
};

const Footer = props => {
  return (
    <Box marginX="auto" w="100%" {...props}>
      <Box textAlign="center" fontSize="xs" pb={2} pt={2} fontWeight="300">
        <Text>Designed & developed by Sumant Guha</Text>
        <Text>&copy; Crafted with ❤️ in Seattle</Text>
      </Box>
    </Box>
  );
};

const Header = ({ props, description, title }) => {
  return (
    <Box marginLeft="20">
      <Heading
        textTransform="uppercase"
        letterSpacing="0.2rem"
        fontSize="xl"
        fontWeight="300"
      >
        {description}
      </Heading>
      <Heading pt="2" fontSize="6xl">
        {title}
      </Heading>
    </Box>
  );
};

export default App;
