import React from 'react';
import {
  Flex,
  Center,
  useColorModeValue,
  Spacer,
  Button,
  Link,
} from '@chakra-ui/react';

import { Document } from 'react-pdf';

import { ColorModeSwitcher } from '../App.js';

const Navbar = props => {
  const bg = useColorModeValue('blue.600', 'yellow.200');
  const color = useColorModeValue('gray.900', 'gray.50');

  const showPdf = () => {
    return <Document file="../Resume.pdf" />;
  };

  return (
    <Flex px="20" py="10" w="100%" {...props}>
      <Button
        fontSize="xl"
        fontWeight="700"
        mr={10}
        transition="0.3s"
        _hover={{ transform: 'scale(1.05)', transition: '0.3s' }}
        variant="outline"
        borderWidth="2px"
        borderColor={bg}
        size="md"
        color={color}
        onClick={() => showPdf}
      >
        Resume
      </Button>
      <Spacer />
      <Center>
        <Link
          href="#about"
          color={color}
          fontSize="2xl"
          fontWeight={700}
          mr={10}
        >
          About
        </Link>
        <Link
          href="#projects"
          color={color}
          fontSize="2xl"
          fontWeight={700}
          mr={10}
        >
          Projects
        </Link>
        <Link
          href="#experience"
          color={color}
          fontSize="2xl"
          fontWeight={700}
          mr={10}
        >
          Work
        </Link>
        <ColorModeSwitcher />
      </Center>
    </Flex>
  );
};

export default Navbar;
