import React from 'react';
import {
  ChakraProvider,
  Container,
  theme,
  Flex,
  Heading,
  Spacer,
  Box,
  useColorMode,
  Button,
  Link,
  Center,
} from '@chakra-ui/react';

import { ColorModeSwitcher } from './ColorModeSwitcher';

import { BiSun } from 'react-icons/bi';

function App() {
  const { colorMode, toggleColorMode } = useColorMode();

  return (
    <ChakraProvider theme={theme}>
      <Container maxW="100%"></Container>
    </ChakraProvider>
  );
}

const Navbar = props => {
  <Flex spacing="3" padding="10" {...props}>
    <Heading fontSize="3xl">Sumant Guha</Heading>
    <Spacer />
    <Center spacing="3">
      <ColorModeSwitcher />
    </Center>
  </Flex>;
};

export default App;
