import React from 'react';
import {
  ChakraProvider,
  Container,
  theme,
  Flex,
  Heading,
  Spacer,
  Box,
  useColorMode,
  Button,
  Link,
  Center,
} from '@chakra-ui/react';

import { ColorModeSwitcher } from './ColorModeSwitcher';

import { BiSun } from 'react-icons/bi';

function App() {
  const { colorMode, toggleColorMode } = useColorMode();

  return (
    <ChakraProvider theme={theme}>
      <Box maxW="100%">
        <Navbar />
      </Box>
    </ChakraProvider>
  );
}

const Navbar = props => {
  return (
    <Flex spacing="3" px="25" py="10" {...props}>
      <Heading fontSize="3xl">Sumant Guha</Heading>
      <Spacer />
      <Center spacing="3">
        <MenuItem>Home</MenuItem>
        <MenuItem>Contact</MenuItem>
        <ColorModeSwitcher />
      </Center>
    </Flex>
  );
};

const MenuItem = ({ children }) => {
  return (
    <Box display="block" mr={10}>
      <Link textDecoration="None" fontSize="xl" fontWeight="700">
        {children}
      </Link>
    </Box>
  );
};

export default App;
