import React from 'react';
import {
  ChakraProvider,
  theme,
  Flex,
  Spacer,
  Box,
  Link,
  Center,
  Text,
  Heading,
  IconButton,
  useColorModeValue,
} from '@chakra-ui/react';
import {
  FaFacebookF,
  FaLinkedinIn,
  FaGithub,
  FaMailBulk,
} from 'react-icons/fa';
import { ColorModeSwitcher } from './ColorModeSwitcher';

function App() {
  return (
    <ChakraProvider theme={theme}>
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
      >
        <Box width="100%">
          <Navbar position="fixed" left="0" top="0" />
          <IntroSection paddingX="20" />
          <Footer position="fixed" left="0" bottom="0" />
        </Box>
      </Box>
    </ChakraProvider>
  );
}

const Navbar = props => {
  return (
    <Flex px="20" py="10" w="100%" {...props}>
      <Box>
        <Link fontWeight="700" fontSize="3xl">
          Sumant Guha
        </Link>
      </Box>
      <Spacer />
      <Center>
        <MenuItem>Home</MenuItem>
        <MenuItem>Contact</MenuItem>
        <ColorModeSwitcher />
      </Center>
    </Flex>
  );
};

const MenuItem = ({ children }) => {
  return (
    <Box display="block" mr={10}>
      <Link textDecoration="None" fontSize="xl" fontWeight="700">
        {children}
      </Link>
    </Box>
  );
};

const IntroSection = props => {
  const bg = useColorModeValue('blue', 'red');
  return (
    <Box maxW="70%" {...props}>
      <Box mb="4">
        <Heading fontSize="6xl">
          Creating and experimenting with high quality content for the internet
          world
        </Heading>
      </Box>
      <Box mt="4">
        <Text
          textTransform="uppercase"
          letterSpacing="0.2rem"
          fontSize="xl"
          fontWeight="200"
        >
          Sumant Guha
        </Text>
        <Box maxW="70%" mx="auto" mt="2">
          <ButtonGroup bg={bg} name={<FaFacebookF />} />
          <ButtonGroup bg={bg} name={<FaLinkedinIn />} />
          <ButtonGroup bg={bg} name={<FaGithub />} />
          <ButtonGroup bg={bg} name={<FaMailBulk />} />
        </Box>
      </Box>
    </Box>
  );
};

const ButtonGroup = ({ props, bg, name }) => {
  return (
    <IconButton
      mt="10"
      size="lg"
      p="5"
      colorScheme={bg}
      fontSize="30px"
      icon={name}
      mr="2rem"
      {...props}
    />
  );
};

const Footer = props => {
  return (
    <Box marginX="auto" w="100%" mb="2" {...props}>
      <Box textAlign="center" fontSize="xs" fontWeight="300">
        <Text>Designed & developed by Sumant Guha</Text>
        <Text>&copy; Crafted with ❤️ in Seattle</Text>
      </Box>
    </Box>
  );
};

export default App;
