import React, { useRef, useEffect } from 'react';
import { Box } from '@chakra-ui/react';

const Canvas = ({ color }) => {
  const canvasRef = useRef(null);

  class Circle {
    constructor(x, y, dx, dy, radius, context) {
      this.x = x;
      this.y = y;
      this.dx = dx;
      this.dy = dy;
      this.context = context;
      this.radius = radius;
      this.startingOpacity = this.opacity = Math.random();
      this.time = 1;
    }

    draw() {
      this.context.beginPath();
      this.context.globalAlpha = 1;
      this.context.arc(this.x, this.y, this.radius, 0, Math.PI * 2, false);
      this.context.fillStyle = color;
      this.context.fill();
    }

    update() {
      if (
        this.x + this.radius > this.context.width ||
        this.x + this.radius < 0
      ) {
        this.dx = -this.dx;
      }

      if (this.opacity > 0) {
        this.opacity -= 0.5;
      } else {
        this.opacity = this.startingOpacity;
      }

      if (
        this.y + this.radius > this.context.height ||
        this.y + this.radius < 0
      ) {
        this.dy = -this.dy;
      }

      this.x += this.dx;
      this.y += this.dy;
      this.time += 1;

      this.draw();
    }
  }

  const resizeCanvasToDisplaySize = canvas => {
    // const { width, height } = canvas.getBoundingClientRect();
    const width = window.innerWidth;
    const height = window.innerHeight;
    console.log(width);
    console.log(height);

    if (canvas.width !== width || canvas.height !== height) {
      canvas.width = width;
      canvas.height = height * 10;
    }
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    resizeCanvasToDisplaySize(canvas);
    let frameCount = 0;
    let animationFrameId;

    let circleArray = [];
    for (let i = 0; i < 75; i++) {
      let radius = Math.random() * 75;
      let x = Math.random() * (canvas.width - radius * 2) + radius;
      let y = Math.random() * (canvas.height - radius * 2) + radius;
      let dx = 1.2 * (Math.random() - 0.5);
      let dy = 1.2 * (Math.random() - 0.5);
      circleArray.push(new Circle(x, y, dx, dy, radius, context));
    }

    const render = () => {
      frameCount++;
      animationFrameId = window.requestAnimationFrame(render);
      context.clearRect(0, 0, canvas.width, canvas.height);
      for (let i = 0; i < circleArray.length; i++) {
        circleArray[i].update();
      }
    };
    render();

    return () => {
      window.cancelAnimationFrame(animationFrameId);
    };
  }, []);

  return (
    <Box zIndex={-5} position="absolute">
      <canvas ref={canvasRef} />
    </Box>
  );
};

export default Canvas;
