import React from 'react';
import {
  Box,
  useDisclosure,
  Image,
  Heading,
  Icon,
  Text,
} from '@chakra-ui/react';

import { FaChevronCircleRight } from 'react-icons/fa';

import ContentModal from './ContentModal';
import { motion } from 'framer-motion';

const Card = ({
  mr,
  mb,
  name,
  start,
  end,
  description,
  image,
  size,
  descriptionSize,
  languages,
}) => {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const handleOpen = () => {
    onOpen();
  };

  const MotionBox = motion.custom(Box);

  return (
    <Box>
      <Box mr={mr} mb={mb}>
        <MotionBox
          onClick={handleOpen}
          role="group"
          position="relative"
          textAlign="left"
          color="white"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <Image
            _groupHover={{ filter: 'brightness(0.3)' }}
            borderRadius={20}
            src={image}
            maxH="90vh"
          />
          <Box p="7" fontSize="5xl" position="absolute" top={0} left={0}>
            <Heading
              className="head"
              textTransform="uppercase"
              letterSpacing="0.2rem"
              fontSize={descriptionSize}
              fontWeight="300"
            >
              {start} {end === null ? '' : `- ${end}`}
            </Heading>
            <Heading pt="2" fontSize={size}>
              {name}
            </Heading>
            <Heading
              pt={size === '5xl' ? 4 : 3}
              textTransform="uppercase"
              letterSpacing="0.2rem"
              fontSize={descriptionSize}
              fontWeight="300"
            >
              {description}
            </Heading>

            <Heading
              pt="7"
              letterSpacing="0.15rem"
              fontSize="2xl"
              visibility="hidden"
              _groupHover={{ visibility: 'visible' }}
            >
              Learn More{' '}
              <Icon
                _groupHover={{ animation: 'blinker 1s linear infinite' }}
                as={FaChevronCircleRight}
              />
            </Heading>
          </Box>
          <Heading position="absolute" left={0} bottom={0} px="7">
            {languages &&
              languages.map(language => {
                return (
                  <Text
                    display="inline-block"
                    fontSize="1.1rem"
                    children={language}
                    mr={4}
                  />
                );
              })}
          </Heading>
        </MotionBox>
      </Box>
      <ContentModal
        mx={10}
        onClose={onClose}
        isOpen={isOpen}
        title={name}
        description={description}
        start={start}
        end={end}
      />
    </Box>
  );
};

export default Card;
