import React from 'react';
import {
  ChakraProvider,
  theme,
  Flex,
  Spacer,
  Box,
  Link,
  Center,
  Text,
  Heading,
  IconButton,
  useColorModeValue,
  Button,
  Image,
} from '@chakra-ui/react';

import me from './images/me.png';

import {
  FaFacebookF,
  FaLinkedinIn,
  FaGithub,
  FaMailBulk,
} from 'react-icons/fa';
import { ColorModeSwitcher } from './ColorModeSwitcher';

function App() {
  return (
    <ChakraProvider theme={theme}>
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
      >
        <Box width="100%">
          <Overlay content={'design'} />
          <Navbar position="fixed" left="0" top="0" />
          <Box display="flex">
            <IntroSection paddingX="20" />
            <ImageSection />
          </Box>
          <Footer position="fixed" left="0" bottom="0" />
        </Box>
      </Box>
    </ChakraProvider>
  );
}

const Overlay = ({ props, content }) => {
  return (
    <Heading
      position="fixed"
      left="20"
      fontSize="400px"
      opacity={0.02}
      color="blue.800"
      textShadow="5"
      textTransform="uppercase"
      {...props}
    >
      {content}
    </Heading>
  );
};

const Navbar = props => {
  const bg = useColorModeValue('blue', 'yellow');
  return (
    <Flex px="20" py="10" w="100%" {...props}>
      <Box>
        <Link fontWeight="700" fontSize="3xl">
          Sumant Guha
        </Link>
      </Box>
      <Spacer />
      <Center>
        <MenuItem>Home</MenuItem>
        <MenuItem>Contact</MenuItem>
        <Button
          // boxShadow="none"
          // variant="outline"
          fontSize="xl"
          fontWeight="700"
          mr={10}
          colorScheme={bg}
          size="md"
        >
          Resume
        </Button>
        <ColorModeSwitcher />
      </Center>
    </Flex>
  );
};

const MenuItem = ({ children }) => {
  return (
    <Box display="block" mr={10}>
      <Link fontSize="xl" fontWeight="700">
        {children}
      </Link>
    </Box>
  );
};

const IntroSection = props => {
  const bg = useColorModeValue('blue', 'yellow');
  const color = useColorModeValue('blue.600', 'yellow.200');
  return (
    <Box maxW="70%" {...props}>
      <Box mb="4">
        <Heading fontSize="6xl" color={color}>
          Creating and experimenting
        </Heading>
        <Heading fontSize="6xl" color={color}>
          with high quality content for
        </Heading>
        <Heading fontSize="6xl" color={color}>
          the internet world
        </Heading>
      </Box>
      <Box mt="4">
        <Text
          textTransform="uppercase"
          letterSpacing="0.2rem"
          fontSize="xl"
          fontWeight="200"
        >
          Sumant Guha
        </Text>
        <Box maxW="70%" mt="2">
          <ButtonGroup bg={bg} name={<FaFacebookF />} />
          <ButtonGroup bg={bg} name={<FaLinkedinIn />} />
          <ButtonGroup bg={bg} name={<FaGithub />} />
          <ButtonGroup bg={bg} name={<FaMailBulk />} />
        </Box>
      </Box>
    </Box>
  );
};

const ButtonGroup = ({ props, bg, name }) => {
  return (
    <IconButton
      mt="10"
      size="lg"
      p="5"
      colorScheme={bg}
      fontSize="30px"
      borderColor={bg}
      shadow="none"
      icon={name}
      mr="2rem"
      {...props}
    />
  );
};

const ImageSection = props => {
  return (
    <Box>
      <Image
        position="absolute"
        maxH="90vh"
        maxW="100%"
        right={0}
        bottom={0}
        src={me}
        alt="Sumant Guha"
        objectFit="cover"
        transform="rotate 180"
      />
    </Box>
  );
};

const Footer = props => {
  return (
    <Box marginX="auto" w="100%" mb="2" {...props}>
      <Box textAlign="center" fontSize="xs" fontWeight="300">
        <Text>Designed & developed by Sumant Guha</Text>
        <Text>&copy; Crafted with ❤️ in Seattle</Text>
      </Box>
    </Box>
  );
};

export default App;
