import React, { useState } from 'react';
import { useColorModeValue, Box, Heading, Text } from '@chakra-ui/react';
import ButtonGroup from '../Components/ButtonGroup';
import { useSpring, animated } from 'react-spring';

import {
  FaFacebookF,
  FaLinkedinIn,
  FaGithub,
  FaMailBulk,
} from 'react-icons/fa';

const IntroSection = props => {
  const [isBooped, setIsBooped] = useState(false);
  const bg = useColorModeValue('blue', 'yellow');
  const color = useColorModeValue('blue.600', 'yellow.200');
  const highlightColor = useColorModeValue('red.500', 'blue.200');
  const AnimatedText = animated(Text);
  const firstLineText = 'Creating and developing';
  const firstLine = [];
  {
    firstLineText.split('').forEach(c => {
      const [style, trigger] = useBoop({ y: 5 });
      firstLine.push(
        <AnimatedText
          display="inline-block"
          onMouseEnter={trigger}
          style={style}
        >
          {c}
        </AnimatedText>
      );
    });
  }
  return (
    <Box>
      <Box maxW="80%" {...props}>
        <Box mb="4">
          <Heading fontSize="8xl" color={color}>
            {firstLine}
          </Heading>
          <Heading fontSize="8xl" color={highlightColor}>
            <Text display="inline-block">high quality</Text>{' '}
            <Text display="inline-block" textDecoration="none" color={color}>
              content for
            </Text>
          </Heading>
          <Heading fontSize="8xl" color={color}>
            the internet world
          </Heading>
        </Box>
        <Box mt="4">
          <Text
            textTransform="uppercase"
            letterSpacing="0.2rem"
            fontSize="xl"
            fontWeight="200"
          >
            creating dynamic and interactive content
          </Text>
        </Box>
      </Box>
      <Box w="100%" textAlign="center">
        <ButtonGroup bg={bg} name={<FaFacebookF />} />
        <ButtonGroup bg={bg} name={<FaLinkedinIn />} />
        <ButtonGroup bg={bg} name={<FaGithub />} />
        <ButtonGroup bg={bg} name={<FaMailBulk />} />
      </Box>
    </Box>
  );
};
function useBoop({
  x = 0,
  y = 0,
  rotation = 0,
  scale = 1,
  timing = 150,
  springConfig = {
    tension: 300,
    friction: 10,
  },
}) {
  // const prefersReducedMotion = usePrefersReducedMotion();
  const [isBooped, setIsBooped] = React.useState(false);
  const style = useSpring({
    transform: isBooped
      ? `translate(${x}px, ${y}px)
         rotate(${rotation}deg)
         scale(${scale})`
      : `translate(0px, 0px)
         rotate(0deg)
         scale(1)`,
    config: springConfig,
  });
  React.useEffect(() => {
    if (!isBooped) {
      return;
    }
    const timeoutId = window.setTimeout(() => {
      setIsBooped(false);
    }, timing);
    return () => {
      window.clearTimeout(timeoutId);
    };
  }, [isBooped]);
  const trigger = React.useCallback(() => {
    setIsBooped(true);
  }, []);
  let appliedStyle = style;
  // let appliedStyle = prefersReducedMotion ? {} : style;
  return [appliedStyle, trigger];
}

export default IntroSection;
