import React, { useRef, useState } from 'react';
import {
  ChakraProvider,
  Flex,
  Spacer,
  Box,
  Link,
  Center,
  Text,
  Heading,
  IconButton,
  useColorModeValue,
  Button,
  Image,
  SimpleGrid,
  extendTheme,
  Collapse,
  Icon,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
} from '@chakra-ui/react';

import me from './images/me.png';
import workingLight from './images/workingLight.svg';
import workingDark from './images/workingDark.svg';
import ta from './images/teaching.svg';
import ey from './images/ml.svg';
import robot from './images/r.svg';
import data from './images/data.svg';

import Projects from './Pages/Projects';
import Card from './Components/Card';
import Header from './Components/Header';

import { motion } from 'framer-motion';

import TA from './Pages/ta';

import {
  FaFacebookF,
  FaLinkedinIn,
  FaGithub,
  FaMailBulk,
} from 'react-icons/fa';

import { CgMouse, CgMenu, CgClose } from 'react-icons/cg';

import { ColorModeSwitcher } from './ColorModeSwitcher';

const config = {
  useSystemColorMode: false,
  initialColorMode: 'light',
};

const pageHandler = {
  'CSE 14x Head Teaching Assistant': <TA />,
};

const customTheme = extendTheme({ config });

function App() {
  const myRef = useRef(null);
  const MotionButton = motion.custom(IconButton);
  return (
    <ChakraProvider theme={customTheme}>
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
        position="relative"
      >
        <MotionButton
          whileTap={{ scale: 0.9 }}
          whileHover={{ scale: 1.2 }}
          position="absolute"
          bottom="2%"
          icon={<CgMouse />}
          variant="none"
          fontSize="1.5rem"
          mx="auto"
          textAlign="center"
          onClick={() => myRef.current.scrollIntoView({ behavior: 'smooth' })}
        />
        <Box width="100%">
          <IntroSection paddingX="20" />
          <ImageSection />
          <Navbar position="absolute" left="0" top="0" />
        </Box>
      </Box>

      <AboutMe scrollRef={myRef} />
      <Projects />
      <Experience mb={5} />
      <Footer position="absolute" left="0" bottom="0" />
    </ChakraProvider>
  );
}

const Experience = props => {
  return (
    <Box {...props} h="auto" pb="20" w="100%">
      <Header description="where i've worked" title="Work Experience" />
      <Flex
        justifyContent="center"
        alignItems="center"
        columns={{ sm: 1, md: 2 }}
        px="20"
        pt="10"
        maxW="100%"
        mx="auto"
        flexWrap="wrap"
      >
        <Card
          mr={20}
          mb={20}
          name="CSE 14x Head Teaching Assistant"
          start="2020"
          end="Present"
          description="University of Washington"
          image={ta}
          size="5xl"
          height={700}
          descriptionSize="xl"
        />
        <Card
          mb={20}
          name="Machine Learning Intern"
          start="2019"
          end={null}
          description="Ernst & Young"
          image={ey}
          size="5xl"
          height={700}
          descriptionSize="xl"
        />

        <Card
          mr={20}
          mb={20}
          name="Embedded Systems Programmer"
          start="2018"
          end="2019"
          description="Husky Robotics"
          image={robot}
          size="5xl"
          height={700}
          descriptionSize="xl"
        />
        <Card
          mb={20}
          name="Data Analytics Intern"
          start="2018"
          end={null}
          description="Mu Sigma"
          image={data}
          size="5xl"
          height={700}
          descriptionSize="xl"
        />
      </Flex>
    </Box>
  );
};

const ContentModal = ({
  props,
  onClose,
  isOpen,
  title,
  description,
  start,
  end,
}) => {
  return (
    <Modal
      {...props}
      onClose={onClose}
      size="6xl"
      isOpen={isOpen}
      justifyContent="center"
      alignItems="center"
      motionPreset="slideInBottom"
      scrollBehavior="inside"
    >
      <ModalOverlay />
      <ModalContent>
        <ModalHeader fontSize="5xl" fontWeight={700}>
          {title}
          <Text
            fontSize="md"
            textTransform="uppercase"
            letterSpacing="0.2rem"
            mt={1}
          >
            {description === undefined ? '' : `${description} |`} {start}{' '}
            {end === null ? '' : `- ${end}`}
          </Text>
        </ModalHeader>

        <ModalCloseButton />
        <ModalBody>{pageHandler[title]}</ModalBody>
        <ModalFooter>
          <Button onClick={onClose}>Close</Button>
        </ModalFooter>
      </ModalContent>
    </Modal>
  );
};

const AboutMe = ({ props, scrollRef }) => {
  const image = useColorModeValue(workingLight, workingDark);
  return (
    <Box w="100%" ref={scrollRef} {...props}>
      <SimpleGrid h="100vh" pt="20" columns={{ sm: 1, md: 2 }} spacing={10}>
        <Box>
          <Header description="learn more" title="About Me" />
          <Text
            marginLeft="20"
            mt="10"
            maxW="80%"
            fontSize="lg"
            fontWeight={300}
          >
            Hello! I'm Sumant, a 20 year old undergraduate student based out of
            Seattle, Washington. Welcome to my slice of the web 😀 <br /> <br />
            I am a junior studying electrical and computer engineering at the
            University of Washington. Passionate about promoting inclusive
            education and computational literacy, I work as a head teaching
            assistant at the Paul G Allen School of Computer Science and
            Engineering for the introductory computer science series. Teaching
            has been one of the rewarding part of my college experience yet!
            <br /> <br /> I work on a variety of projects across multiple
            platforms such as: Python, React, Java, ReactNative, JavaScript,
            HTML, CSS and C++. Take a look at some of my projects below to see
            my work 💻
          </Text>
        </Box>
        <Center marginRight="20" maxW="80%" maxH="80%">
          <Image src={image} />
        </Center>
      </SimpleGrid>
    </Box>
  );
};

const Navbar = props => {
  const bg = useColorModeValue('blue.600', 'yellow.200');
  return (
    <Flex px="20" py="10" w="100%" {...props}>
      <Box>
        <Link fontWeight="700" fontSize="3xl">
          Sumant Guha
        </Link>
      </Box>
      <Spacer />
      <Center>
        <Button
          fontSize="xl"
          fontWeight="700"
          mr={10}
          // colorScheme={bg}
          variant="ghost"
          borderWidth="2px"
          borderColor={bg}
          size="md"
        >
          Resume
        </Button>
        <ColorModeSwitcher mr={10} />
        <Hamburger />
      </Center>
    </Flex>
  );
};

const Hamburger = props => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOpen = () => {
    setIsOpen(!isOpen);
    if (!isOpen) {
      disableScroll();
    } else {
      enableScroll();
    }
  };

  const disableScroll = () => {
    let x = window.scrollX;
    let y = window.scrollY;
    window.onscroll = () => {
      window.scrollTo(x, y);
    };
  };

  const enableScroll = () => {
    window.onscroll = () => {};
  };

  return (
    <Box {...props}>
      <Collapse in={isOpen} unmountOnExit={true} animateOpacity>
        <Box
          hidden={!isOpen}
          position="absolute"
          top="0"
          left="0"
          w="100%"
          bg="tomato"
          h="100vh"
        >
          <Center h="100vh" w="100%" textAlign="center" color="white">
            <Box>
              <Heading
                textTransform="uppercase"
                letterSpacing="0.2rem"
                fontSize="xl"
                fontWeight="300"
                mb={3}
              >
                Navigate
              </Heading>
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Home
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                About Me
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Teaching
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Experience
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Projects
              </Link>
            </Box>
          </Center>
        </Box>
      </Collapse>
      <IconButton
        fontSize="4xl"
        icon={!isOpen ? <CgMenu /> : <CgClose />}
        variant="none"
        onClick={handleOpen}
      />
    </Box>
  );
};

const IntroSection = props => {
  const bg = useColorModeValue('blue', 'yellow');
  const color = useColorModeValue('blue.600', 'yellow.200');
  const highlightColor = useColorModeValue('red.500', 'blue.200');

  return (
    <Box maxW="70%" {...props}>
      <Box mb="4">
        <Heading fontSize="6xl" color={color}>
          Creating and developing
        </Heading>
        <Heading fontSize="6xl" color={highlightColor}>
          <Text display="inline-block">high quality</Text>{' '}
          <Text display="inline-block" textDecoration="none" color={color}>
            content for
          </Text>
        </Heading>
        <Heading fontSize="6xl" color={color}>
          the internet world
        </Heading>
      </Box>
      <Box mt="4">
        <Text
          textTransform="uppercase"
          letterSpacing="0.2rem"
          fontSize="xl"
          fontWeight="200"
        >
          Sumant Guha
        </Text>

        <Box maxW="70%" mt="2" mx="auto">
          <ButtonGroup bg={bg} name={<FaFacebookF />} />
          <ButtonGroup bg={bg} name={<FaLinkedinIn />} />
          <ButtonGroup bg={bg} name={<FaGithub />} />
          <ButtonGroup bg={bg} name={<FaMailBulk />} />
        </Box>
      </Box>
    </Box>
  );
};

const ButtonGroup = ({ props, bg, name }) => {
  const MotionButton = motion.custom(IconButton);
  return (
    <MotionButton
      whileHover={{ scale: 1.3 }}
      whileTap={{ scale: 0.9 }}
      mt="10"
      size="lg"
      p="5"
      colorScheme={bg}
      fontSize="30px"
      icon={name}
      mr="2rem"
      {...props}
    />
  );
};

const ImageSection = props => {
  return (
    <Box>
      <Image
        position="absolute"
        maxH="80vh"
        maxW="100%"
        right={0}
        bottom={0}
        src={me}
        alt="Sumant Guha"
        objectFit="cover"
        userdrag="none"
        userSelect="none"
      />
    </Box>
  );
};

const Footer = props => {
  return (
    <Box marginX="auto" w="100%" {...props}>
      <Box textAlign="center" fontSize="xs" pt={2} fontWeight="300">
        <Text>Designed & developed by Sumant Guha</Text>
        <Text>&copy; Crafted with ❤️ in Seattle</Text>
      </Box>
    </Box>
  );
};

export default App;
