import React from 'react';
import {
  ChakraProvider,
  theme,
  Flex,
  Spacer,
  Box,
  Link,
  Center,
  Text,
} from '@chakra-ui/react';
import { BiSun } from 'react-icons/bi';
import { ColorModeSwitcher } from './ColorModeSwitcher';

function App() {
  return (
    <ChakraProvider theme={theme}>
      <Box maxW="100%" h="100vh">
        <Navbar />
        <Footer position="fixed" left="0" bottom="0" justifySelf="center" />
      </Box>
    </ChakraProvider>
  );
}

const Navbar = props => {
  return (
    <Flex spacing="3" px="20" py="10" {...props}>
      <Link fontWeight="700" fontSize="3xl">
        Sumant Guha
      </Link>
      <Spacer />
      <Center spacing="3">
        <MenuItem>Home</MenuItem>
        <MenuItem>Contact</MenuItem>
        <ColorModeSwitcher />
      </Center>
    </Flex>
  );
};

const MenuItem = ({ children }) => {
  return (
    <Box display="block" mr={10}>
      <Link textDecoration="None" fontSize="xl" fontWeight="700">
        {children}
      </Link>
    </Box>
  );
};

const Footer = props => {
  return (
    <Box
      maxW="100%"
      textAlign="center"
      fontSize="xs"
      fontWeight="200"
      {...props}
    >
      <Text>Designed & developed by Sumant</Text>
      <Text>&copy; Crafted with ❤️ in Seattle</Text>
    </Box>
  );
};

export default App;
