import React, { useRef } from 'react';
import { ChakraProvider, Box, IconButton, extendTheme } from '@chakra-ui/react';

import Projects from './Pages/Projects';
import Experience from './Pages/Experience';
import AboutMe from './Pages/AboutMe';
import Navbar from './Components/Navbar';
import IntroSection from './Pages/IntroSection';
import Footer from './Components/Footer';
import ImageSection from './Components/ImageSection';
import Teaching from './Pages/Teaching';

import { motion } from 'framer-motion';

import { CgMouse } from 'react-icons/cg';

const config = {
  useSystemColorMode: false,
  initialColorMode: 'light',
};

const customTheme = extendTheme({ config });

const App = () => {
  const myRef = useRef(null);
  const MotionButton = motion.custom(IconButton);
  return (
    <ChakraProvider theme={customTheme}>
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
        position="relative"
      >
        <MotionButton
          whileTap={{ scale: 0.9 }}
          whileHover={{ scale: 1.2 }}
          position="absolute"
          bottom="2%"
          icon={<CgMouse />}
          variant="none"
          fontSize="1.5rem"
          mx="auto"
          textAlign="center"
          onClick={() => myRef.current.scrollIntoView({ behavior: 'smooth' })}
        />
        <Box width="100%">
          <IntroSection paddingX="20" />
          <ImageSection />
          <Navbar position="absolute" left="0" top="0" />
        </Box>
      </Box>

      <AboutMe scrollRef={myRef} />
      <Projects h="auto" pb="20" w="100%" />
      <Experience h="auto" pb="20" mb={20} w="100%" />
      <Footer position="absolute" left="0" bottom="0" pb={10} h="auto" />
    </ChakraProvider>
  );
};

export default App;
