import React from 'react';
import {
  Box,
  ModalBody,
  Button,
  Heading,
  Link,
  Code,
  useColorModeValue,
} from '@chakra-ui/react';
import { BsPlay, BsFileEarmarkCode } from 'react-icons/bs';

const Portfolio = props => {
  const firstColor = useColorModeValue('red', 'yellow');
  const secondColor = useColorModeValue('blue.600', 'yellow.200');
  return (
    <Box {...props}>
      <ModalBody>
        <Button leftIcon={<BsFileEarmarkCode />} colorScheme="blue" size="lg">
          <Link
            as="a"
            _hover={{ textDecor: 'none' }}
            href="https://github.com/sumantguha/portfolio"
            isExternal={true}
          >
            View Source Code
          </Link>
        </Button>
        <br />
        <br />
        <Heading display="inline-block" mr={2} size="sm">
          Development Frameworks:
        </Heading>
        <Code children="React.js" mr={2} />
        <br />
        <br />
        An older version of this website! Not hosted anywhere but feel free to
        look through the source code. Designed
      </ModalBody>
    </Box>
  );
};

export default Portfolio;
