import React, { useRef } from 'react';
import { ChakraProvider, Box, IconButton, extendTheme } from '@chakra-ui/react';

import Projects from './Pages/Projects';
import Experience from './Pages/Experience';
import AboutMe from './Pages/AboutMe';
import Navbar from './Components/Navbar';
import IntroSection from './Pages/IntroSection';
import Footer from './Components/Footer';
import ImageSection from './Components/ImageSection';
import ParticlesBg from 'particles-bg';

import { motion } from 'framer-motion';

import { CgMouse } from 'react-icons/cg';

const config = {
  useSystemColorMode: false,
  initialColorMode: 'light',
};

const bgConfig = {
  num: [4, 7],
  rps: 0.1,
  radius: [5, 40],
  life: [1.5, 3],
  v: [2, 3],
  tha: [-40, 40],
  // body: "./img/icon.png", // Whether to render pictures
  rotate: [0, 20],
  alpha: [0.1, 0],
  scale: [1, 0.1],
  position: 'all', // all or center or {x:1,y:1,width:100,height:100}
  color: ['random', '#ff0000'],
  cross: 'dead', // cross or bround
  random: 15, // or null,
  g: 5, // gravity
  // f: [2, -1], // force
  onParticleUpdate: (ctx, particle) => {
    ctx.beginPath();
    ctx.circle(
      particle.p.x,
      particle.p.y,
      particle.radius * 2,
      particle.radius * 2
    );
    ctx.fillStyle = particle.color;
    ctx.fill();
    ctx.closePath();
  },
};

const customTheme = extendTheme({ config });

const App = () => {
  const myRef = useRef(null);
  const MotionButton = motion.custom(IconButton);
  return (
    <ChakraProvider theme={customTheme}>
      <ParticlesBg type="custom" config={bgConfig} bg={true} />
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
        position="relative"
      >
        <MotionButton
          whileTap={{ scale: 0.9 }}
          whileHover={{ scale: 1.2 }}
          position="absolute"
          bottom="2%"
          icon={<CgMouse />}
          variant="none"
          fontSize="1.5rem"
          mx="auto"
          textAlign="center"
          onClick={() => myRef.current.scrollIntoView({ behavior: 'smooth' })}
        />
        <Box width="100%">
          <IntroSection paddingX="20" />
          <ImageSection />
          <Navbar position="absolute" left="0" top="0" />
        </Box>
      </Box>

      <AboutMe scrollRef={myRef} />
      <Experience h="auto" pb="20" w="100%" />
      <Projects h="auto" pb="20" w="100%" />
      {/* <Contact h="auto" pb="20" w="100%" /> */}
      <Footer position="absolute" left="0" bottom="0" />
    </ChakraProvider>
  );
};

export default App;
