import React, { useRef, useState } from 'react';
import {
  ChakraProvider,
  Flex,
  Spacer,
  Box,
  Link,
  Center,
  Text,
  Heading,
  IconButton,
  useColorModeValue,
  Button,
  Image,
  SimpleGrid,
  extendTheme,
  Collapse,
  Icon,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
} from '@chakra-ui/react';

import me from './images/me.png';
import workingLight from './images/workingLight.svg';
import workingDark from './images/workingDark.svg';
import ta from './images/teaching.svg';
import ey from './images/ml.svg';
import robot from './images/r.svg';
import data from './images/data.svg';
import tt from './images/tt.svg';
import webapps from './images/webapps.svg';
import gradeit from './images/gradeit.svg';
import maps from './images/maps.svg';
import insurance from './images/insurance.svg';

import { motion } from 'framer-motion';

import TA from './Pages/ta';

import {
  FaFacebookF,
  FaLinkedinIn,
  FaGithub,
  FaMailBulk,
  FaChevronCircleRight,
} from 'react-icons/fa';

import { CgMouse, CgMenu, CgClose } from 'react-icons/cg';

import { ColorModeSwitcher } from './ColorModeSwitcher';

const config = {
  useSystemColorMode: false,
  initialColorMode: 'light',
};

const pageHandler = {
  'CSE 14x Head Teaching Assistant': <TA />,
};

const customTheme = extendTheme({ config });

function App() {
  const myRef = useRef(null);
  const MotionButton = motion.custom(IconButton);
  return (
    <ChakraProvider theme={customTheme}>
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
        position="relative"
      >
        <MotionButton
          whileTap={{ scale: 0.9 }}
          whileHover={{ scale: 1.2 }}
          position="absolute"
          bottom="2%"
          icon={<CgMouse />}
          variant="none"
          fontSize="1.5rem"
          mx="auto"
          textAlign="center"
          onClick={() => myRef.current.scrollIntoView({ behavior: 'smooth' })}
        />
        <Box width="100%">
          <IntroSection paddingX="20" />
          <ImageSection />
          <Navbar position="absolute" left="0" top="0" />
        </Box>
      </Box>

      <AboutMe scrollRef={myRef} />
      <Projects />
      <Experience mb={10} />
      <Footer position="absolute" left="0" bottom="0" />
    </ChakraProvider>
  );
}

const Projects = props => {
  return (
    <Box {...props} h="auto" pb="20" w="100%">
      <Header description="case studies" title="Projects" />
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
      >
        <SimpleGrid
          justifyContent="center"
          alignItems="center"
          columns={{ sm: 1, md: 2 }}
          px="20"
          pt="10"
          maxW="100%"
          mx="auto"
        >
          <Card
            mr={20}
            mb={20}
            name="Code.it: HighSchool Education Platform"
            start="2020"
            end="Present"
            image={tt}
            size="4xl"
            height={500}
          />

          <Card
            mr={20}
            mb={20}
            name="Nifty Web Apps: Build web apps for any programming assignment"
            start="2020"
            end="Present"
            image={webapps}
            size="4xl"
            height={500}
          />
        </SimpleGrid>

        <SimpleGrid columns={{ sm: 1, md: 3 }} px="20" maxW="100%">
          <Card
            mr={5}
            mb={10}
            name="Java Code Quality Linter (CSE 14x)"
            start="2020"
            end="Present"
            image={insurance}
            size="3xl"
            height={500}
          />

          <Card
            ml={5}
            mr={5}
            mb={10}
            name="GradeIt Database Migration"
            start="2020"
            end="Present"
            image={gradeit}
            size="3xl"
            height={500}
          />

          <Card
            ml={5}
            mr={5}
            mb={10}
            name="Husky Maps Visualization"
            start="2019"
            end="2020"
            image={maps}
            size="3xl"
            height={500}
          />
        </SimpleGrid>
      </Box>
    </Box>
  );
};

const Experience = props => {
  return (
    <Box {...props} h="auto" pb="20" w="100%">
      <Header description="where i've worked" title="Work Experience" />
      <Flex
        justifyContent="center"
        alignItems="center"
        columns={{ sm: 1, md: 2 }}
        px="20"
        pt="10"
        maxW="100%"
        mx="auto"
        flexWrap="wrap"
      >
        <Card
          mr={20}
          mb={20}
          name="CSE 14x Head Teaching Assistant"
          start="2020"
          end="Present"
          place="University of Washington"
          image={ta}
          size="5xl"
          height={700}
        />
        <Card
          mb={20}
          name="Machine Learning Intern"
          start="2019"
          end={null}
          place="Ernst & Young"
          image={ey}
          size="5xl"
          height={700}
        />

        <Card
          mr={20}
          mb={20}
          name="Embedded Systems Programmer"
          start="2018"
          end="2019"
          place="Husky Robotics"
          image={robot}
          size="5xl"
          height={700}
        />
        <Card
          mb={20}
          name="Data Analytics Intern"
          start="2018"
          end={null}
          place="Mu Sigma"
          image={data}
          size="5xl"
          height={700}
        />
      </Flex>
    </Box>
  );
};

const Card = ({ mr, mb, name, start, end, place, image, size }) => {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const handleOpen = () => {
    onOpen();
  };

  return (
    <Box>
      <Box mr={mr} mb={mb}>
        <Box
          onClick={handleOpen}
          role="group"
          position="relative"
          textAlign="left"
          color="white"
        >
          <Image
            _groupHover={{ filter: 'brightness(0.3)' }}
            borderRadius={20}
            src={image}
            maxH="90vh"
          />
          <Box p="7" fontSize="5xl" position="absolute" top={0} left={0}>
            <Heading
              className="head"
              textTransform="uppercase"
              letterSpacing="0.2rem"
              fontSize="xl"
              fontWeight="300"
            >
              {start} {end === null ? '' : `- ${end}`}
            </Heading>
            <Heading pt="2" fontSize={size}>
              {name}
            </Heading>
            <Heading
              pt={4}
              textTransform="uppercase"
              letterSpacing="0.2rem"
              fontSize="xl"
              fontWeight="300"
            >
              {place}
            </Heading>
            <Heading
              pt="7"
              letterSpacing="0.15rem"
              fontSize="2xl"
              visibility="hidden"
              _groupHover={{ visibility: 'visible' }}
            >
              Learn More{' '}
              <Icon
                _groupHover={{ animation: 'blinker 1s linear infinite' }}
                as={FaChevronCircleRight}
              />
            </Heading>
          </Box>
        </Box>
      </Box>
      <ContentModal
        mx={10}
        onClose={onClose}
        isOpen={isOpen}
        title={name}
        description={place}
        start={start}
        end={end}
      />
    </Box>
  );
};

const ContentModal = ({
  props,
  onClose,
  isOpen,
  title,
  description,
  start,
  end,
}) => {
  return (
    <Modal
      {...props}
      onClose={onClose}
      size="6xl"
      isOpen={isOpen}
      justifyContent="center"
      alignItems="center"
      motionPreset="slideInBottom"
      scrollBehavior="inside"
    >
      <ModalOverlay />
      <ModalContent>
        <ModalHeader fontSize="5xl" fontWeight={700}>
          {title}
          <Text
            fontSize="md"
            textTransform="uppercase"
            letterSpacing="0.2rem"
            mt={1}
          >
            {description === undefined ? '' : `${description} |`} {start}{' '}
            {end === null ? '' : `- ${end}`}
          </Text>
        </ModalHeader>

        <ModalCloseButton />
        <ModalBody>{pageHandler[title]}</ModalBody>
        <ModalFooter>
          <Button onClick={onClose}>Close</Button>
        </ModalFooter>
      </ModalContent>
    </Modal>
  );
};

const AboutMe = ({ props, scrollRef }) => {
  const image = useColorModeValue(workingLight, workingDark);
  return (
    <Box w="100%" ref={scrollRef} {...props}>
      <SimpleGrid h="100vh" pt="20" columns={{ sm: 1, md: 2 }} spacing={10}>
        <Box>
          <Header description="learn more" title="About Me" />
          <Text
            marginLeft="20"
            mt="10"
            maxW="80%"
            fontSize="lg"
            fontWeight={300}
          >
            Hello! I'm Sumant, a 20 year old undergraduate student based out of
            Seattle, Washington. Welcome to my slice of the web  <br /> <br />
            I am a junior studying electrical and computer engineering at the
            University of Washington. Passionate about promoting inclusive
            education and computational literacy, I work as a head teaching
            assistant at the Paul G Allen School of Computer Science and
            Engineering for the introductory computer science series. Teaching
            has been one of the rewarding part of my college experience yet!
            <br /> <br /> I work on a variety of projects across multiple
            platforms such as: Python, React, Java, ReactNative, JavaScript,
            HTML, CSS and C++. Take a look at some of my projects below to see
            my work 
          </Text>
        </Box>
        <Center marginRight="20" maxW="80%" maxH="80%">
          <Image src={image} />
        </Center>
      </SimpleGrid>
    </Box>
  );
};

const Navbar = props => {
  const bg = useColorModeValue('blue.600', 'yellow.200');
  return (
    <Flex px="20" py="10" w="100%" {...props}>
      <Box>
        <Link fontWeight="700" fontSize="3xl">
          Sumant Guha
        </Link>
      </Box>
      <Spacer />
      <Center>
        <Button
          fontSize="xl"
          fontWeight="700"
          mr={10}
          // colorScheme={bg}
          variant="ghost"
          borderWidth="2px"
          borderColor={bg}
          size="md"
        >
          Resume
        </Button>
        <ColorModeSwitcher mr={10} />
        <Hamburger />
      </Center>
    </Flex>
  );
};

const Hamburger = props => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOpen = () => {
    setIsOpen(!isOpen);
    if (!isOpen) {
      disableScroll();
    } else {
      enableScroll();
    }
  };

  const disableScroll = () => {
    let x = window.scrollX;
    let y = window.scrollY;
    window.onscroll = () => {
      window.scrollTo(x, y);
    };
  };

  const enableScroll = () => {
    window.onscroll = () => {};
  };

  return (
    <Box {...props}>
      <Collapse in={isOpen} unmountOnExit={true} animateOpacity>
        <Box
          hidden={!isOpen}
          position="absolute"
          top="0"
          left="0"
          w="100%"
          bg="tomato"
          h="100vh"
        >
          <Center h="100vh" w="100%" textAlign="center" color="white">
            <Box>
              <Heading
                textTransform="uppercase"
                letterSpacing="0.2rem"
                fontSize="xl"
                fontWeight="300"
                mb={3}
              >
                Navigate
              </Heading>
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Home
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                About Me
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Teaching
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Experience
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Projects
              </Link>
            </Box>
          </Center>
        </Box>
      </Collapse>
      <IconButton
        fontSize="4xl"
        icon={!isOpen ? <CgMenu /> : <CgClose />}
        variant="none"
        onClick={handleOpen}
      />
    </Box>
  );
};

const IntroSection = props => {
  const bg = useColorModeValue('blue', 'yellow');
  const color = useColorModeValue('blue.600', 'yellow.200');
  const highlightColor = useColorModeValue('red.500', 'blue.200');

  return (
    <Box maxW="70%" {...props}>
      <Box mb="4">
