import React, { useRef, useState } from 'react';
import {
  ChakraProvider,
  Box,
  Link,
  Center,
  Text,
  Heading,
  IconButton,
  useColorModeValue,
  Image,
  extendTheme,
  Collapse,
} from '@chakra-ui/react';

import me from './images/me.png';

import Projects from './Pages/Projects';
import Experience from './Pages/Experience';
import AboutMe from './Pages/AboutMe';
import Navbar from './Components/Navbar';

import { motion } from 'framer-motion';

import {
  FaFacebookF,
  FaLinkedinIn,
  FaGithub,
  FaMailBulk,
} from 'react-icons/fa';

import { CgMouse, CgMenu, CgClose } from 'react-icons/cg';

import { ColorModeSwitcher } from './ColorModeSwitcher';

const config = {
  useSystemColorMode: false,
  initialColorMode: 'light',
};

const customTheme = extendTheme({ config });

const App = () => {
  const myRef = useRef(null);
  const MotionButton = motion.custom(IconButton);
  return (
    <ChakraProvider theme={customTheme}>
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
        position="relative"
      >
        <MotionButton
          whileTap={{ scale: 0.9 }}
          whileHover={{ scale: 1.2 }}
          position="absolute"
          bottom="2%"
          icon={<CgMouse />}
          variant="none"
          fontSize="1.5rem"
          mx="auto"
          textAlign="center"
          onClick={() => myRef.current.scrollIntoView({ behavior: 'smooth' })}
        />
        <Box width="100%">
          <IntroSection paddingX="20" />
          <ImageSection />
          <Navbar position="absolute" left="0" top="0" />
        </Box>
      </Box>

      <AboutMe scrollRef={myRef} />
      <Projects />
      <Experience mb={5} />
      <Footer position="absolute" left="0" bottom="0" />
    </ChakraProvider>
  );
};

const Hamburger = props => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOpen = () => {
    setIsOpen(!isOpen);
    if (!isOpen) {
      disableScroll();
    } else {
      enableScroll();
    }
  };

  const disableScroll = () => {
    let x = window.scrollX;
    let y = window.scrollY;
    window.onscroll = () => {
      window.scrollTo(x, y);
    };
  };

  const enableScroll = () => {
    window.onscroll = () => {};
  };

  return (
    <Box {...props}>
      <Collapse in={isOpen} unmountOnExit={true} animateOpacity>
        <Box
          hidden={!isOpen}
          position="absolute"
          top="0"
          left="0"
          w="100%"
          bg="tomato"
          h="100vh"
        >
          <Center h="100vh" w="100%" textAlign="center" color="white">
            <Box>
              <Heading
                textTransform="uppercase"
                letterSpacing="0.2rem"
                fontSize="xl"
                fontWeight="300"
                mb={3}
              >
                Navigate
              </Heading>
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Home
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                About Me
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Teaching
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Experience
              </Link>
              <br />
              <Link fontWeight="700" pt="2" fontSize="6xl">
                Projects
              </Link>
            </Box>
          </Center>
        </Box>
      </Collapse>
      <IconButton
        fontSize="4xl"
        icon={!isOpen ? <CgMenu /> : <CgClose />}
        variant="none"
        onClick={handleOpen}
      />
    </Box>
  );
};

const IntroSection = props => {
  const bg = useColorModeValue('blue', 'yellow');
  const color = useColorModeValue('blue.600', 'yellow.200');
  const highlightColor = useColorModeValue('red.500', 'blue.200');

  return (
    <Box maxW="70%" {...props}>
      <Box mb="4">
        <Heading fontSize="6xl" color={color}>
          Creating and developing
        </Heading>
        <Heading fontSize="6xl" color={highlightColor}>
          <Text display="inline-block">high quality</Text>{' '}
          <Text display="inline-block" textDecoration="none" color={color}>
            content for
          </Text>
        </Heading>
        <Heading fontSize="6xl" color={color}>
          the internet world
        </Heading>
      </Box>
      <Box mt="4">
        <Text
          textTransform="uppercase"
          letterSpacing="0.2rem"
          fontSize="xl"
          fontWeight="200"
        >
          Sumant Guha
        </Text>

        <Box maxW="70%" mt="2" mx="auto">
          <ButtonGroup bg={bg} name={<FaFacebookF />} />
          <ButtonGroup bg={bg} name={<FaLinkedinIn />} />
          <ButtonGroup bg={bg} name={<FaGithub />} />
          <ButtonGroup bg={bg} name={<FaMailBulk />} />
        </Box>
      </Box>
    </Box>
  );
};

const ButtonGroup = ({ props, bg, name }) => {
  const MotionButton = motion.custom(IconButton);
  return (
    <MotionButton
      whileHover={{ scale: 1.3 }}
      whileTap={{ scale: 0.9 }}
      mt="10"
      size="lg"
      p="5"
      colorScheme={bg}
      fontSize="30px"
      icon={name}
      mr="2rem"
      {...props}
    />
  );
};

const ImageSection = props => {
  return (
    <Box>
      <Image
        position="absolute"
        maxH="80vh"
        maxW="100%"
        right={0}
        bottom={0}
        src={me}
        alt="Sumant Guha"
        objectFit="cover"
        userdrag="none"
        userSelect="none"
      />
    </Box>
  );
};

const Footer = props => {
  return (
    <Box marginX="auto" w="100%" {...props}>
      <Box textAlign="center" fontSize="xs" pt={2} fontWeight="300">
        <Text>Designed & developed by Sumant Guha</Text>
        <Text>&copy; Crafted with ❤️ in Seattle</Text>
      </Box>
    </Box>
  );
};

export default App;
