import React from 'react';
import {
  ChakraProvider,
  theme,
  Flex,
  Spacer,
  Box,
  Link,
  Center,
  Text,
  Heading,
  IconButton,
  useColorModeValue,
  Button,
  Image,
  SimpleGrid,
} from '@chakra-ui/react';

import me from './images/me.png';

import {
  FaFacebookF,
  FaLinkedinIn,
  FaGithub,
  FaMailBulk,
} from 'react-icons/fa';
import { ColorModeSwitcher } from './ColorModeSwitcher';

function App() {
  return (
    <ChakraProvider theme={theme}>
      <Box
        m="0 auto 0 auto"
        display="flex"
        alignItems="center"
        justifyContent="center"
        maxW="100%"
        h="100vh"
        position="relative"
      >
        <Box width="100%">
          {/* <Overlay content={'design'} /> */}
          <IntroSection paddingX="20" />
          <ImageSection />
          <Navbar position="absolute" left="0" top="0" />
        </Box>
      </Box>
      <AboutMe />
      <Footer position="absolute" left="0" bottom="0" />
    </ChakraProvider>
  );
}

const AboutMe = () => {
  return (
    <Box h="100vh" w="100%">
      <SimpleGrid pt="20" columns={2} spacing={1}>
        <Box bg="tomato" paddingX="20">
          <Heading
            textTransform="uppercase"
            letterSpacing="0.2rem"
            fontSize="l"
            fontWeight="200"
          >
            Learn More
          </Heading>
          <Heading pt="3" fontSize="6xl">
            About me
          </Heading>
        </Box>
        <Box marginRight="20" bg="tomato" height="80px">
          Something interesting
        </Box>
      </SimpleGrid>
    </Box>
  );
};

const Overlay = ({ props, content }) => {
  const color = useColorModeValue('gray.400', 'gray.700');
  return (
    <Heading
      position="fixed"
      left="20"
      fontSize="400px"
      opacity={0.05}
      color={color}
      textShadow="5"
      textTransform="lowercase"
      {...props}
    >
      {content}
    </Heading>
  );
};

const Navbar = props => {
  const bg = useColorModeValue('blue.600', 'yellow.200');
  return (
    <Flex px="20" py="10" w="100%" {...props}>
      <Box>
        <Link fontWeight="700" fontSize="3xl">
          Sumant Guha
        </Link>
      </Box>
      <Spacer />
      <Center>
        <MenuItem>Home</MenuItem>
        <MenuItem>Contact</MenuItem>
        <Button
          fontSize="xl"
          fontWeight="700"
          mr={10}
          // colorScheme={bg}
          variant="ghost"
          borderWidth="2px"
          borderColor={bg}
          size="md"
        >
          Resume
        </Button>
        <ColorModeSwitcher />
      </Center>
    </Flex>
  );
};

const MenuItem = ({ children }) => {
  return (
    <Box display="block" mr={10}>
      <Link fontSize="xl" fontWeight="700">
        {children}
      </Link>
    </Box>
  );
};

const IntroSection = props => {
  const bg = useColorModeValue('blue', 'yellow');
  const color = useColorModeValue('blue.600', 'yellow.200');
  const highlightColor = useColorModeValue('red.500', 'blue.200');
  return (
    <Box maxW="70%" {...props}>
      <Box mb="4">
        <Heading fontSize="6xl" color={color}>
          Creating and developing
        </Heading>
        <Heading fontSize="6xl" color={highlightColor}>
          <Text display="inline-block">high quality</Text>{' '}
          <Text display="inline-block" textDecoration="none" color={color}>
            content for
          </Text>
        </Heading>
        <Heading fontSize="6xl" color={color}>
          the internet world
        </Heading>
      </Box>
      <Box mt="4">
        <Text
          textTransform="uppercase"
          letterSpacing="0.2rem"
          fontSize="xl"
          fontWeight="200"
        >
          Sumant Guha
        </Text>

        <Box maxW="70%" mt="2" mx="auto">
          <ButtonGroup bg={bg} name={<FaFacebookF />} />
          <ButtonGroup bg={bg} name={<FaLinkedinIn />} />
          <ButtonGroup bg={bg} name={<FaGithub />} />
          <ButtonGroup bg={bg} name={<FaMailBulk />} />
        </Box>
      </Box>
    </Box>
  );
};

const ButtonGroup = ({ props, bg, name }) => {
  return (
    <IconButton
      mt="10"
      size="lg"
      p="5"
      colorScheme={bg}
      fontSize="30px"
      icon={name}
      mr="2rem"
      {...props}
    />
  );
};

const ImageSection = props => {
  return (
    <Box>
      <Image
        position="absolute"
        maxH="80vh"
        maxW="100%"
        right={0}
        bottom={0}
        src={me}
        alt="Sumant Guha"
        objectFit="cover"
        userDrag="none"
        userSelect="none"
      />
    </Box>
  );
};

const Footer = props => {
  return (
    <Box marginX="auto" w="100%" {...props}>
      <Box textAlign="center" fontSize="xs" pb={2} pt={2} fontWeight="300">
        <Text>Designed & developed by Sumant Guha</Text>
        <Text>&copy; Crafted with ❤️ in Seattle</Text>
      </Box>
    </Box>
  );
};

export default App;
