import React from 'react';
import {
  Box,
  useDisclosure,
  Image,
  Heading,
  Icon,
  Badge,
} from '@chakra-ui/react';

import { FaChevronCircleRight } from 'react-icons/fa';

import ContentModal from './ContentModal';
import { motion } from 'framer-motion';

const Card = ({
  mr,
  mb,
  name,
  start,
  end,
  description,
  image,
  size,
  descriptionSize,
  newPost,
}) => {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const handleOpen = () => {
    onOpen();
  };

  const MotionBox = motion.custom(Box);

  return (
    <Box>
      <Box mr={mr} mb={mb}>
        <MotionBox
          onClick={handleOpen}
          role="group"
          position="relative"
          textAlign="left"
          color="white"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <Image
            _groupHover={{ filter: 'brightness(0.3)' }}
            borderRadius={20}
            src={image}
            maxH="90vh"
          />
          <Box p="7" fontSize="5xl" position="absolute" top={0} left={0}>
            <Heading
              className="head"
              textTransform="uppercase"
              letterSpacing="0.2rem"
              fontSize={descriptionSize}
              fontWeight="300"
            >
              {start} {end === null ? '' : `- ${end}`}
            </Heading>
            <Heading pt="2" fontSize={size}>
              <svg
                width="100"
                height="100"
                viewBox="0 0 100 100"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <circle
                  cx="50"
                  cy="50"
                  r="25"
                  stroke="white"
                  stroke-width="5"
                />
              </svg>
              {name}
            </Heading>
            <Heading
              pt={size === '5xl' ? 4 : 3}
              textTransform="uppercase"
              letterSpacing="0.2rem"
              fontSize={descriptionSize}
              fontWeight="300"
            >
              {description}
            </Heading>

            <Box position="relative" w="100%" h={size}>
              {newPost && (
                <Badge
                  position="fixed"
                  top={7}
                  right={7}
                  fontSize="1.2rem"
                  colorScheme="purple"
                  visibility="hidden"
                  _groupHover={{ visibility: 'visible' }}
                >
                  New
                </Badge>
              )}
            </Box>

            <Heading
              pt="7"
              letterSpacing="0.15rem"
              fontSize="2xl"
              visibility="hidden"
              _groupHover={{ visibility: 'visible' }}
            >
              Learn More{' '}
              <Icon
                _groupHover={{ animation: 'blinker 1s linear infinite' }}
                as={FaChevronCircleRight}
              />
            </Heading>
          </Box>
        </MotionBox>
      </Box>
      <ContentModal
        mx={10}
        onClose={onClose}
        isOpen={isOpen}
        title={name}
        description={description}
        start={start}
        end={end}
      />
    </Box>
  );
};

export default Card;
